mapper.insert:

insert into recharge_log (id, user_id, type, 
  status, amount, real_amount, 
  order_no, add_time, update_time
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{type,jdbcType=TINYINT}, 
  #{status,jdbcType=TINYINT}, #{amount,jdbcType=DECIMAL}, #{realAmount,jdbcType=DECIMAL}, 
  #{orderNo,jdbcType=VARCHAR}, #{addTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select t.id, t.user_id, t.type, t.status, t.amount, t.real_amount, t.order_no, t.add_time, t.update_time,r.mobile
from recharge_log t inner join user r on  t.user_id = r.id



        and t.type = #{type}


        and t.status = #{status}


        and t.add_time >= #{startTime}


        and t.add_time < #{endTime}


        and (t.order_no = #{queryName} or r.mobile = #{queryName})


        and t.user_id = #{userId}
