mapper.select:

select 

from system_menu t
where t.deleted = false and  t.id = #{id,jdbcType=INTEGER}

SELECT from system_menu t LEFT JOIN system_role_menu s on t.id = s.menu_id LEFT JOIN system_operator_role o on s.role_id = o.role_id
/*查询左侧主菜单,即屏蔽按钮菜单*/ t.main_menu = TRUE and
o.user_id = #{userId}
order by t.pid = 0 desc,t.sort asc
