mapper.select:
select 
  from discount_coupon


          and type = #{type}




                  /*未使用必须要没有过期*/
                  and status = 0 and end_time > #{now}


                  /*冻结,已使用都默认为使用*/
                  and status in (1,2)


                  /*过期*/
                  and status = 3 or end_time <= #{now}




          and user_id = #{userId}
